<?php namespace Elementor;class ElementsKit_Extend_Onepage_Scroll{public function __construct(){add_action('elementor/element/post/document_settings/after_section_end',[$this,'register_page_controls']);add_action('elementor/element/section/section_advanced/after_section_end',[$this,'register_section_controls']);}public function register_page_controls(Controls_Stack $element){$element->start_controls_section('ekit_page_settings',['label'=>esc_html__('ElementsKit Settings','elementskit'),'tab'=>Controls_Manager::TAB_SETTINGS,]);$element->add_control('ekit_onepagescroll',['label'=>esc_html__('Enable Onepage Scroll','elementskit'),'type'=>Controls_Manager::SWITCHER,'frontend_available'=>true,]);if(\ElementsKit::PACKAGE_TYPE==='pro'){$this->controls_pro_onepagescroll($element);}$element->end_controls_section();}public function register_section_controls(Controls_Stack $element){$element->start_controls_section('ekit_onepagescroll_section',['label'=>esc_html__('ElementsKit Onepage Scroll','elementskit'),'tab'=>Controls_Manager::TAB_ADVANCED,]);$element->add_control('ekit_has_onepagescroll',['label'=>esc_html__('Normal Scroll Section','elementskit'),'type'=>Controls_Manager::SWITCHER,'default'=>'','return_value'=>'normal-scroll','prefix_class'=>'fp-',]);$element->end_controls_section();}public function controls_pro_onepagescroll($element){$element->add_control('ekit_onepagescroll_heading',['label'=>__('Onepage Scroll Settings','elementskit'),'type'=>\Elementor\Controls_Manager::HEADING,'separator'=>'before','condition'=>['ekit_onepagescroll'=>'yes'],]);$element->add_control('ekit_onepagescroll_nav',['label'=>esc_html__('Navigation Style','elementskit'),'type'=>Controls_Manager::SELECT,'default'=>'0','options'=>['0'=>'None','1'=>'Style 1','2'=>'Style 2',],'frontend_available'=>true,'condition'=>['ekit_onepagescroll'=>'yes']]);}}